<?php/** * Church Theme Framework * by churchthemes.com * * The framework provides code and assets common to multiple themes for more organized and efficient development/updates. * It is intended for use in themes that use the Church Content Manager plugin. *//******************************************** * CONSTANTS ********************************************//** * Theme Data */$theme_data = wp_get_theme();// Get parent theme data if child themeif ( is_child_theme() ) {	$theme_data = wp_get_theme( $theme_data->template );}/** * Universal Constants * * These are used by both the theme and framework. */// Framework Dataif ( ! defined( 'CTFW_VERSION' ) )	define( 'CTFW_VERSION', 	'0.8.7' ); // Theme Dataif ( ! defined( 'CTC_NAME' ) )			define( 'CTC_NAME', 		$theme_data->Name );					// parent theme name, specified in style.cssif ( ! defined( 'CTC_VERSION' ) )		define( 'CTC_VERSION', 		$theme_data->Version );					// parent theme versionif ( ! defined( 'CTC_AUTHOR' ) )		define( 'CTC_AUTHOR', 		strip_tags( $theme_data->Author ) ); 	// parent theme's authorif ( ! defined( 'CTC_TEMPLATE' ) )		define( 'CTC_TEMPLATE', 	$theme_data->template );				// parent theme's folder (theme slug)// Parent/Child Theme Constantsif ( ! defined( 'CTC_THEME_PATH' ) )	define( 'CTC_THEME_PATH', 	get_template_directory() );				// parent theme pathif ( ! defined( 'CTC_THEME_URL' ) )		define( 'CTC_THEME_URL', 	get_template_directory_uri() );			// parent theme URIif ( ! defined( 'CTC_CHILD_PATH' ) )	define( 'CTC_CHILD_PATH', 	get_stylesheet_directory() );			// child theme pathif ( ! defined( 'CTC_CHILD_URL' ) )		define( 'CTC_CHILD_URL', 	get_stylesheet_directory_uri() );		// child theme URI// Theme Directories// (theme and framework structure mirror each other)if ( ! defined( 'CTC_INC_DIR' ) )		define( 'CTC_INC_DIR',		'includes' );							// includes directoryif ( ! defined( 'CTC_ADMIN_DIR' ) )		define( 'CTC_ADMIN_DIR',	CTC_INC_DIR . '/admin' );				// admin directoryif ( ! defined( 'CTC_CLASS_DIR' ) )		define( 'CTC_CLASS_DIR', 	CTC_INC_DIR . '/classes' );				// classes directoryif ( ! defined( 'CTC_LIB_DIR' ) )		define( 'CTC_LIB_DIR', 		CTC_INC_DIR . '/libraries' );			// libraries directoryif ( ! defined( 'CTC_PAGE_TPL_DIR' ) )	define( 'CTC_PAGE_TPL_DIR',	'page-templates' );						// page templates directoryif ( ! defined( 'CTC_WIDGET_DIR' ) )	define( 'CTC_WIDGET_DIR', 	'widget-templates' );					// widget templates directoryif ( ! defined( 'CTC_CSS_DIR' ) )		define( 'CTC_CSS_DIR', 		'css' );								// stylesheets directoryif ( ! defined( 'CTC_JS_DIR' ) )		define( 'CTC_JS_DIR', 		'js' );									// JavaScript directoryif ( ! defined( 'CTC_IMG_DIR' ) )		define( 'CTC_IMG_DIR', 		'images' );								// images directoryif ( ! defined( 'CTC_COLOR_DIR' ) )		define( 'CTC_COLOR_DIR',	'colors' );								// color schemes directoryif ( ! defined( 'CTC_LANG_DIR' ) )		define( 'CTC_LANG_DIR', 	'languages' );							// languages directory// Framework Directories// (theme and framework structure mirror each other)if ( ! defined( 'CTFW_DIR' ) )			define( 'CTFW_DIR',			basename( dirname( __FILE__) ) );		// framework directory (where this file is)if ( ! defined( 'CTFW_INC_DIR' ) )		define( 'CTFW_INC_DIR',		CTFW_DIR . '/' . CTC_INC_DIR );			// framework includes directoryif ( ! defined( 'CTFW_ADMIN_DIR' ) )	define( 'CTFW_ADMIN_DIR',	CTFW_DIR . '/' . CTC_ADMIN_DIR );		// framework admin directoryif ( ! defined( 'CTFW_CLASS_DIR' ) )	define( 'CTFW_CLASS_DIR',	CTFW_DIR . '/' . CTC_CLASS_DIR );		// framework classes directoryif ( ! defined( 'CTFW_LIB_DIR' ) )		define( 'CTFW_LIB_DIR',		CTFW_DIR . '/' . CTC_LIB_DIR );			// framework libraries directoryif ( ! defined( 'CTFW_CSS_DIR' ) )		define( 'CTFW_CSS_DIR',		CTFW_DIR . '/' . CTC_CSS_DIR );			// framework stylesheets directoryif ( ! defined( 'CTFW_JS_DIR' ) )		define( 'CTFW_JS_DIR',		CTFW_DIR . '/' . CTC_JS_DIR );			// framework JavaScript directoryif ( ! defined( 'CTFW_IMG_DIR' ) )		define( 'CTFW_IMG_DIR',		CTFW_DIR . '/' . CTC_IMG_DIR );			// framework images directory/******************************************** * INCLUDES ********************************************//** * Include Files */// Functions$ctfw_includes = array(	// Frontend or Admin	'always' => array(			// Functions		CTFW_INC_DIR . '/localization.php',		CTFW_INC_DIR . '/support.php',		CTFW_INC_DIR . '/compatibility.php',		CTFW_INC_DIR . '/conditions.php',		CTFW_INC_DIR . '/customize.php',		CTFW_INC_DIR . '/background.php',		CTFW_INC_DIR . '/colors.php',		CTFW_INC_DIR . '/fonts.php',		CTFW_INC_DIR . '/files.php',		CTFW_INC_DIR . '/media.php',		CTFW_INC_DIR . '/head.php',		CTFW_INC_DIR . '/menus.php',			CTFW_INC_DIR . '/content.php',		CTFW_INC_DIR . '/post-formats.php',		CTFW_INC_DIR . '/pagination.php',		CTFW_INC_DIR . '/templates.php',		CTFW_INC_DIR . '/template-tags.php',		CTFW_INC_DIR . '/comments.php',		CTFW_INC_DIR . '/sidebars.php',		CTFW_INC_DIR . '/widgets.php',		CTFW_INC_DIR . '/taxonomies.php',		CTFW_INC_DIR . '/posts.php',		CTFW_INC_DIR . '/pages.php',		CTFW_INC_DIR . '/archives.php',		CTFW_INC_DIR . '/sermons.php',		CTFW_INC_DIR . '/locations.php',		CTFW_INC_DIR . '/events.php',		CTFW_INC_DIR . '/people.php',		CTFW_INC_DIR . '/helpers.php',		CTFW_INC_DIR . '/maps.php',		CTFW_INC_DIR . '/shortcodes.php',		CTFW_INC_DIR . '/deprecated.php',				// Classes		CTFW_CLASS_DIR . '/customize-controls.php',		CTFW_CLASS_DIR . '/widget.php',		CTFW_CLASS_DIR . '/Mobile_Detect.php',			),	// Admin Only	'admin' => array(			// Functions		CTFW_ADMIN_DIR . '/activation.php',		CTFW_ADMIN_DIR . '/edd-license.php',		CTFW_ADMIN_DIR . '/admin-css.php',		CTFW_ADMIN_DIR . '/admin-js.php',		CTFW_ADMIN_DIR . '/admin-menu.php',		CTFW_ADMIN_DIR . '/admin-widgets.php',		CTFW_ADMIN_DIR . '/meta-boxes.php',				// Libraries		CTFW_LIB_DIR . '/ct-meta-box/ct-meta-box.php',	),		// Frontend Only	'frontend' => array (		// Classes		CTFW_CLASS_DIR . '/walker-nav-menu-description.php',		CTFW_CLASS_DIR . '/breadcrumbs.php',	),);// Include Files$ctfw_includes = apply_filters( 'ctfw_includes', $ctfw_includes ); // make filterablectc_load_includes( $ctfw_includes );/** * Include Loader Function * * Used by framework above and functions.php for theme-specific includes. * If include exists in child theme, it will be used. Otherwise, parent theme file is used. */function ctc_load_includes( $includes ) {			// Loop conditions	foreach ( $includes as $condition => $files ) {			// Check condition		$do_includes = false;		switch( $condition ) {						// Admin Only			case 'admin':							if ( is_admin() ) {					$do_includes = true;				}								break;							// Frontend Only			case 'frontend':							if ( ! is_admin() ) {					$do_includes = true;				}								break;							// Admin or Frontend (always)			default:							$do_includes = true;								break;								}			// Loop files if condition met		if ( $do_includes ) {					foreach( $files as $file ) {					locate_template( $file, true ); // include from child theme first, then parent theme			}					}			}}